import React from 'react';
import { Stack, Typography, Box } from '@mui/material';
import 'swiper/css'; 
import 'swiper/css/effect-fade';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Scrollbar, Autoplay } from 'swiper/modules';
import { premiumToursServices } from '../../Database/db';
import PremiumItem from './PremiumItem';

const PremiumTours = () => {

  return (
    <Stack height={'730px'}
      width={'100%'}
      padding={'80px 35px'}
      alignItems={'center'}
      justifyContent={'center'}
      bgcolor={'#F97316'}
      boxSizing={'border-box'}
    >
      <Stack
        height={'100%'}
        width={'100%'}
        justifyContent={'space-between'}
        alignItems={'center'}
        paddingX={'20px'}
        gap={'1rem'}
      >
        {/* Title */}
        <Typography
          fontFamily={'Poppins'}
          width={'95%'}
          fontSize={'48px'}
          lineHeight={'48px'}
          fontWeight={'bold'}
          textAlign={'center'}
          height={'auto'}
          sx={{
            '@media (max-width: 900px)': {
              fontSize: '36px',
              lineHeight: '40px',
            },
            '@media (max-width: 600px)': {
              fontSize: '24px',
              lineHeight: '28px',
            },
          }}
        >
          Bizning Premium Turlarimizni Kashf Etish
        </Typography>

        {/* Slider */}
        <Box display={'flex'} 
             alignItems={'center'} 
             justifyContent={'center'} 
             height={'384px'} 
             width={'100%'}
             padding={'0.5rem'}>
          <Swiper
            modules={[Navigation, Pagination, Scrollbar, Autoplay]}
            spaceBetween={50}
            slidesPerView={3}
            autoplay={{ delay: 2500, disableOnInteraction: false }}
            navigation
            pagination={{ clickable: false }}
            scrollbar={{ draggable: true }}
            loop={true}
            style={{
              position: 'relative',
              width: '100%', 
              height: '100%',
            }}
            breakpoints={{
              600: {
                slidesPerView: 1,
                spaceBetween: 10,
              },
              900: {
                slidesPerView: 2,
                spaceBetween: 20,
              },
              1200: {
                slidesPerView: 3,
                spaceBetween: 50,
              },
            }}
          >
            {
              premiumToursServices?.map((tour, idx) => {
                return (
                  <SwiperSlide key={idx}>
                    <PremiumItem tour={tour} />
                  </SwiperSlide>
                );
              })
            }
          </Swiper>
        </Box>
      </Stack>
    </Stack>
  );
};

export default PremiumTours;
